			#interior
			if row[2] == 1
				tile = Entity::Tile.new
				type = Entity::TileType.where(name: row[4] + ' (Inside)').first
				tile.type = type
				tile.name = row[3]
				tile.x = row[0]
				tile.y = row[1]
				tile.description = row[6]
				tile.z = 1
				tile.plane = 2
				tile.save
			end
		end
	end

end